package LogicalOperator;

public class Equal_to_Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 5;
		int b = 10;
		int c = 15;
		String s1 = "Arvind";
		String s2 = "Pawar";
		String s3 = "Arvind";
		System.out.println("Displaying Variables A: "+a);
		System.out.println("Displaying Variables B: "+b);
		System.out.println("Displaying Variables C: "+c);
		//Comparing a and b
		System.out.println("a ==b: "+(a==b));
		//comparing a and c
		System.out.println("a ==c: "+(a==c));
		System.out.println("S1==s2: "+(s1==s2));
		System.out.println("s1==s3: "+(s1==s3));
	}	
}

