package UninaryOperator;

public class Example1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		System.out.println(a++); // print then assign  Post increment 
		System.out.println(a);  // print value
		System.out.println(++a); // assign then print       // Pre-increment
		System.out.println("--------------------------------");
		
		int b = 20;
		System.out.println(b--);     // print then assign  Post Decrement
		System.out.println(b);
		System.out.println(--b);     //Pre-Decrement
	}

}
