package UninaryOperator;

public class UnnaryOpertorExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b = 10;
		System.out.println(a++ + ++a); //Pre-Increment & Post Increment
		System.out.println(b++ + ++b);
		
		System.out.println("---------------------------");
		
		System.out.println(a-- - --a); // preDecrement & Post Decrement
		System.out.println(b-- - --b);
	}

}
