package casting;

public class ExplicitCastingExample1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double d = 45;   // higher data types 
		// converting into lower data types
		int a = (int) d;
		System.out.println(d); // higher data type
		
		System.out.println(a);  // lower data type

	}

}
